import { useState, useEffect, useCallback, useRef } from 'react';
import './index.css';
import { getEmployees, getIssues, submitWeek1, submitWeek234, addIssues, updateStatus } from './api';

// ΓöÇΓöÇΓöÇ FORMAT DOJ HELPER ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function formatDOJ(raw) {
  if (!raw) return 'ΓÇö';
  const str = String(raw);
  // Try to parse date strings and return DD-MM-YYYY
  try {
    const d = new Date(str);
    if (!isNaN(d.getTime())) {
      const dd = String(d.getDate()).padStart(2, '0');
      const mm = String(d.getMonth() + 1).padStart(2, '0');
      const yyyy = d.getFullYear();
      return `${dd}-${mm}-${yyyy}`;
    }
  } catch (_) { /* ignore */ }
  return str;
}

// ΓöÇΓöÇΓöÇ MAIN APP ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
export default function App() {
  const [page, setPage] = useState('dashboard');
  const [employees, setEmployees] = useState([]);
  const [issues, setIssues] = useState([]);
  const [loading, setLoading] = useState(true);
  const [toast, setToast] = useState('');

  const loadAll = useCallback(async () => {
    setLoading(true);
    try {
      const [emps, iss] = await Promise.all([getEmployees(), getIssues()]);
      setEmployees(emps || []);
      setIssues(iss || []);
    } catch (e) { alert('Load error: ' + e.message); }
    setLoading(false);
  }, []);

  useEffect(() => { loadAll(); }, [loadAll]);

  const showToast = (msg) => { setToast(msg); setTimeout(() => setToast(''), 3000); };

  const tabs = [
    { id: 'dashboard', label: '≡ƒôè Dashboard' },
    { id: 'week1', label: '≡ƒô¥ Week 1 Form' },
    { id: 'week234', label: '≡ƒôï Week 2-4 Form' },
    { id: 'tracker', label: '≡ƒÄ» Issue Tracker' },
    { id: 'raise', label: 'Γ₧ò Raise Issues / Support Tickets' },
  ];

  return (
    <>
      {/* Header */}
      <header className="glass" style={{ padding: '1.25rem', marginBottom: '1.5rem' }}>
        <div className="flex-between">
          <div>
            <h1 className="gradient-text" style={{ fontSize: '1.75rem', fontWeight: 700 }}>HR Employee Connect</h1>
            <p style={{ color: 'var(--text-muted)', fontSize: '0.875rem', marginTop: '0.25rem' }}>Issue Tracking & Resolution System</p>
          </div>
        </div>
        <div className="nav-tabs">
          {tabs.map(t => (
            <button key={t.id} className={`nav-tab ${page === t.id ? 'active' : ''}`} onClick={() => setPage(t.id)}>{t.label}</button>
          ))}
        </div>
      </header>

      {/* Loading */}
      {loading && <div className="flex-center" style={{ flex: 1 }}><div className="spinner" /></div>}

      {/* Pages */}
      {!loading && page === 'dashboard' && <Dashboard issues={issues} onStatusChange={async (tid, st) => { await updateStatus(tid, st, ''); showToast(`≡ƒöä ${tid} ΓåÆ ${st}`); loadAll(); }} onResolve={(tid, notes) => updateStatus(tid, 'Resolved', notes).then(() => { showToast(`Γ£à ${tid} Resolved!`); loadAll(); })} />}
      {!loading && page === 'week1' && <Week1Form employees={employees} onSubmit={async (d) => { await submitWeek1(d); showToast('Γ£à Week 1 response saved!'); }} />}
      {!loading && page === 'week234' && <Week234Form employees={employees} onSubmit={async (d) => { await submitWeek234(d); showToast('Γ£à Week 2-4 response saved!'); }} />}
      {!loading && page === 'tracker' && <IssueTracker issues={issues} onStatusChange={async (tid, st) => { await updateStatus(tid, st, ''); showToast(`≡ƒöä ${tid} ΓåÆ ${st}`); loadAll(); }} onResolve={(tid, notes) => updateStatus(tid, 'Resolved', notes).then(() => { showToast(`Γ£à ${tid} Resolved!`); loadAll(); })} />}
      {!loading && page === 'raise' && <RaiseIssue employees={employees} onSubmit={async (data) => { const res = await addIssues(data); showToast(`≡ƒÄ½ ${res.count} ticket(s) created: ${res.ticketIDs.join(', ')}`); loadAll(); }} />}

      {/* Toast */}
      {toast && <div className="toast">{toast}</div>}
    </>
  );
}

// ΓöÇΓöÇΓöÇ DASHBOARD ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function Dashboard({ issues, onStatusChange, onResolve }) {
  const [resolveModal, setResolveModal] = useState(null);
  const [resolveNotes, setResolveNotes] = useState('');
  const [activeFilter, setActiveFilter] = useState('Needs Attention');

  const getFilteredIssues = () => {
    switch (activeFilter) {
      case 'Total Tickets': return issues;
      case 'Red': return issues.filter(i => i['Flag Level'] === 'Red');
      case 'Yellow': return issues.filter(i => i['Flag Level'] === 'Yellow');
      case 'Open': return issues.filter(i => i.Status === 'Open');
      case 'Resolved': return issues.filter(i => i.Status === 'Resolved');
      default: return issues.filter(i => i.Status !== 'Resolved');
    }
  };

  const displayIssues = getFilteredIssues();

  return (
    <div className="space-y">
      <div className="grid-5">
        <div className="glass stat-card hover-card" style={{ cursor: 'pointer', border: activeFilter === 'Total Tickets' ? '1px solid var(--purple-400)' : '' }} onClick={() => setActiveFilter(activeFilter === 'Total Tickets' ? 'Needs Attention' : 'Total Tickets')}><p className="stat-value">{issues.length}</p><p className="stat-label">Total Tickets</p></div>
        <div className="glass stat-card hover-card" style={{ cursor: 'pointer', border: activeFilter === 'Red' ? '1px solid var(--red-400)' : '' }} onClick={() => setActiveFilter(activeFilter === 'Red' ? 'Needs Attention' : 'Red')}><p className="stat-value" style={{ color: 'var(--red-400)' }}>{issues.filter(i => i['Flag Level'] === 'Red').length}</p><p className="stat-label">≡ƒö┤ Red</p></div>
        <div className="glass stat-card hover-card" style={{ cursor: 'pointer', border: activeFilter === 'Yellow' ? '1px solid var(--yellow-400)' : '' }} onClick={() => setActiveFilter(activeFilter === 'Yellow' ? 'Needs Attention' : 'Yellow')}><p className="stat-value" style={{ color: 'var(--yellow-400)' }}>{issues.filter(i => i['Flag Level'] === 'Yellow').length}</p><p className="stat-label">≡ƒƒí Yellow</p></div>
        <div className="glass stat-card hover-card" style={{ cursor: 'pointer', border: activeFilter === 'Open' ? '1px solid var(--orange-400)' : '' }} onClick={() => setActiveFilter(activeFilter === 'Open' ? 'Needs Attention' : 'Open')}><p className="stat-value" style={{ color: 'var(--orange-400)' }}>{issues.filter(i => i.Status === 'Open').length}</p><p className="stat-label">ΓÅ│ Open</p></div>
        <div className="glass stat-card hover-card" style={{ cursor: 'pointer', border: activeFilter === 'Resolved' ? '1px solid var(--emerald-400)' : '' }} onClick={() => setActiveFilter(activeFilter === 'Resolved' ? 'Needs Attention' : 'Resolved')}><p className="stat-value" style={{ color: 'var(--emerald-400)' }}>{issues.filter(i => i.Status === 'Resolved').length}</p><p className="stat-label">Γ£à Resolved</p></div>
      </div>

      <div className="glass" style={{ padding: '1.25rem' }}>
        <h2 style={{ fontSize: '1.125rem', fontWeight: 700, marginBottom: '1rem' }}>ΓÜí {activeFilter === 'Needs Attention' ? 'Open Issues (Needs Attention)' : activeFilter + ' Issues'}</h2>
        <div className="space-y-sm">
          {displayIssues.length === 0 && <p style={{ textAlign: 'center', color: 'var(--text-muted)', padding: '2rem' }}>≡ƒÄë No issues found for this filter!</p>}
          {displayIssues.slice(0, 20).map(issue => (
            <div key={issue['Ticket ID']} className="ticket-row flex-between" style={{ flexWrap: 'wrap', gap: '0.75rem' }}>
              <div style={{ flex: 1, minWidth: '200px' }}>
                <div className="flex-gap" style={{ marginBottom: '0.25rem' }}>
                  <span style={{ fontSize: '0.75rem', fontFamily: 'monospace', color: 'var(--purple-400)' }}>{issue['Ticket ID']}</span>
                  <span className={`badge badge-${issue['Flag Level'] === 'Red' ? 'red' : 'yellow'}`}>{issue['Flag Level']}</span>
                  <span className={`badge ${issue.Type === 'Issue' ? 'badge-red' : 'badge-blue'}`}>{issue.Type}</span>
                </div>
                <p style={{ fontSize: '0.875rem', fontWeight: 500 }}>{issue['Employee Name']}</p>
                <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', marginTop: '0.25rem' }}>{issue.Description}</p>
              </div>
              <div className="flex-gap">
                <button className="btn btn-xs" style={{ background: 'rgba(250,204,21,0.2)', color: 'var(--yellow-400)' }} onClick={() => onStatusChange(issue['Ticket ID'], 'In Progress')}>≡ƒöä In Progress</button>
                <button className="btn btn-xs" style={{ background: 'rgba(52,211,153,0.2)', color: 'var(--emerald-400)' }} onClick={() => { setResolveModal(issue); setResolveNotes(''); }}>Γ£à Resolve</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Resolve Modal */}
      {resolveModal && (
        <div className="modal-overlay" onClick={() => setResolveModal(null)}>
          <div className="modal-box" onClick={e => e.stopPropagation()}>
            <h3 style={{ fontSize: '1.125rem', fontWeight: 700, color: 'var(--emerald-400)', marginBottom: '1rem' }}>Γ£à Resolve: {resolveModal['Ticket ID']}</h3>
            <p style={{ fontSize: '0.875rem', color: 'var(--text-secondary)' }}>{resolveModal['Employee Name']}</p>
            <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', marginBottom: '1rem' }}>{resolveModal.Description}</p>
            <label className="label">Resolution Notes *</label>
            <textarea className="g-input" rows={3} value={resolveNotes} onChange={e => setResolveNotes(e.target.value)} placeholder="How was this resolved?" />
            <div className="flex-gap" style={{ marginTop: '1rem' }}>
              <button className="btn btn-success" style={{ flex: 1 }} onClick={() => { if (!resolveNotes) return alert('Add notes'); onResolve(resolveModal['Ticket ID'], resolveNotes); setResolveModal(null); }}>Mark Resolved</button>
              <button className="btn btn-ghost" onClick={() => setResolveModal(null)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// ΓöÇΓöÇΓöÇ EMPLOYEE SELECT + AUTOFILL HOOK ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function useEmployeeSelect(employees) {
  const empty = { screeningID: '', name: '', designation: '', doj: '' };
  const [selected, setSelected] = useState(empty);

  const selectEmployee = (emp) => {
    setSelected({ screeningID: emp.screeningID, name: emp.name, designation: emp.designation, doj: emp.doj });
  };

  const reset = () => setSelected({ ...empty });

  return { selected, selectEmployee, reset };
}

// ΓöÇΓöÇΓöÇ SEARCHABLE EMPLOYEE DROPDOWN (Search by Name) ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function EmployeeSearchDropdown({ employees, selected, onSelect, onClear }) {
  const [query, setQuery] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const wrapperRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (wrapperRef.current && !wrapperRef.current.contains(e.target)) setIsOpen(false);
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const filtered = employees.filter(emp =>
    emp.name.toLowerCase().includes(query.toLowerCase())
  );

  const handleSelect = (emp) => {
    onSelect(emp);
    setQuery('');
    setIsOpen(false);
  };

  const handleClear = () => {
    onClear();
    setQuery('');
    setIsOpen(false);
  };

  // If employee is already selected, show their name with a clear button
  if (selected.screeningID) {
    return (
      <div>
        <label className="label">Search Employee *</label>
        <div className="g-input" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', cursor: 'pointer' }}>
          <span style={{ fontWeight: 500 }}>{selected.name}</span>
          <button type="button" onClick={handleClear} style={{ background: 'none', border: 'none', color: 'var(--red-400)', cursor: 'pointer', fontSize: '0.875rem', padding: '0 0.25rem' }}>Γ£ò</button>
        </div>
      </div>
    );
  }

  return (
    <div ref={wrapperRef} style={{ position: 'relative' }}>
      <label className="label">Search Employee by Name *</label>
      <div style={{ position: 'relative' }}>
        <input
          className="g-input"
          style={{ paddingRight: '2rem' }}
          value={query}
          onChange={e => { setQuery(e.target.value); setIsOpen(true); }}
          onFocus={() => setIsOpen(true)}
          placeholder="Click to browse or type to search..."
          autoComplete="off"
        />
        <span
          onClick={() => setIsOpen(!isOpen)}
          style={{
            position: 'absolute', right: '0.75rem', top: '50%', transform: 'translateY(-50%)',
            cursor: 'pointer', color: 'var(--text-muted)', fontSize: '0.7rem', userSelect: 'none'
          }}
        >{isOpen ? 'Γû▓' : 'Γû╝'}</span>
      </div>
      {isOpen && (
        <div style={{
          position: 'absolute', top: '100%', left: 0, right: 0, zIndex: 50,
          maxHeight: '260px', overflowY: 'auto',
          background: 'rgba(15, 23, 42, 0.97)', border: '1px solid rgba(139, 92, 246, 0.3)',
          borderRadius: '0.75rem', marginTop: '0.25rem',
          boxShadow: '0 10px 40px rgba(0,0,0,0.5)'
        }}>
          <div style={{ padding: '0.5rem 1rem', borderBottom: '1px solid rgba(255,255,255,0.08)', fontSize: '0.7rem', color: 'var(--text-muted)' }}>
            {filtered.length} employee{filtered.length !== 1 ? 's' : ''} {query ? 'matching' : 'available'}
          </div>
          {filtered.length === 0 && (
            <div style={{ padding: '0.75rem 1rem', color: 'var(--text-muted)', fontSize: '0.8rem' }}>No matches found</div>
          )}
          {filtered.map(emp => (
            <div
              key={emp.screeningID}
              onClick={() => handleSelect(emp)}
              style={{
                padding: '0.625rem 1rem', cursor: 'pointer',
                borderBottom: '1px solid rgba(255,255,255,0.05)',
                transition: 'background 0.15s'
              }}
              onMouseEnter={e => e.currentTarget.style.background = 'rgba(139, 92, 246, 0.15)'}
              onMouseLeave={e => e.currentTarget.style.background = 'transparent'}
            >
              <p style={{ fontSize: '0.875rem', fontWeight: 500, color: '#fff' }}>{emp.name}</p>
              <p style={{ fontSize: '0.7rem', color: 'var(--text-muted)', marginTop: '0.125rem' }}>{emp.screeningID} ΓÇó {emp.designation}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

// ΓöÇΓöÇΓöÇ EMPLOYEE AUTO-FILLED INFO ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function EmployeeInfo({ data }) {
  return (
    <div className="grid-4">
      <div><label className="label">Screening ID</label><p className="info-value" style={{ fontFamily: 'monospace', color: 'var(--purple-400)', fontSize: '0.8rem' }}>{data.screeningID || 'ΓÇö'}</p></div>
      <div><label className="label">Name</label><p className="info-value">{data.name || 'ΓÇö'}</p></div>
      <div><label className="label">Designation</label><p className="info-value">{data.designation || 'ΓÇö'}</p></div>
      <div><label className="label">DOJ</label><p className="info-value">{formatDOJ(data.doj)}</p></div>
    </div>
  );
}

// ΓöÇΓöÇΓöÇ WEEK 1 FORM ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function Week1Form({ employees, onSubmit }) {
  const { selected: emp, selectEmployee, reset: resetEmp } = useEmployeeSelect(employees);
  const [day, setDay] = useState('');
  const [jobRole, setJobRole] = useState('');
  const [comfort, setComfort] = useState('');
  const [support, setSupport] = useState('');
  const [location, setLocation] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!emp.screeningID || !day) return alert('Select employee and day.');
    setSubmitting(true);
    try {
      await onSubmit({ ...emp, day, jobRole, comfort, support, location });
      resetEmp(); setDay(''); setJobRole(''); setComfort(''); setSupport(''); setLocation('');
    } catch (err) { alert(err.message); }
    setSubmitting(false);
  };

  return (
    <div className="glass" style={{ padding: '1.25rem' }}>
      <h2 style={{ fontSize: '1.125rem', fontWeight: 700, color: 'var(--purple-400)', marginBottom: '1rem' }}>≡ƒô¥ Week 1 Daily Touchpoint Form</h2>
      <form onSubmit={handleSubmit} className="space-y-sm">
        <div className="grid-3">
          <EmployeeSearchDropdown employees={employees} selected={emp} onSelect={selectEmployee} onClear={resetEmp} />
          <div>
            <label className="label">Day *</label>
            <select className="g-input" value={day} onChange={e => setDay(e.target.value)} required>
              <option value="">-- Select Day --</option>
              <option value="Day 1">Day 1 ΓÇö Basics & IT</option>
              <option value="Day 2">Day 2 ΓÇö Role Clarity</option>
              <option value="Day 3">Day 3 ΓÇö Comfort & Travel</option>
              <option value="Day 4">Day 4 ΓÇö System & Process</option>
              <option value="Day 5">Day 5 ΓÇö Workload Review</option>
              <option value="Day 6">Day 6 ΓÇö Week 1 Summary</option>
            </select>
          </div>
          <div><label className="label">Location</label><input className="g-input" value={location} onChange={e => setLocation(e.target.value)} placeholder="KOLKATA, DETROJ..." /></div>
        </div>
        <EmployeeInfo data={emp} />
        <div className="grid-2">
          <div><label className="label">Discussion on Job Role</label><textarea className="g-input" rows={2} value={jobRole} onChange={e => setJobRole(e.target.value)} placeholder="Responsibilities, clarity, team coordination..." /></div>
          <div><label className="label">Personal Comfort / Discomfort</label><textarea className="g-input" rows={2} value={comfort} onChange={e => setComfort(e.target.value)} placeholder="Travel, accommodation, food, workspace..." /></div>
        </div>
        <div><label className="label">Additional Support / Clarity / System Needed</label><textarea className="g-input" rows={2} value={support} onChange={e => setSupport(e.target.value)} placeholder="Laptop, email ID, software, mediclaim..." /></div>
        <button className="btn btn-primary" style={{ width: '100%' }} disabled={submitting}>{submitting ? 'Submitting...' : 'Submit Week 1 Response'}</button>
      </form>
    </div>
  );
}

// ΓöÇΓöÇΓöÇ WEEK 2-4 FORM ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function Week234Form({ employees, onSubmit }) {
  const { selected: emp, selectEmployee, reset: resetEmp } = useEmployeeSelect(employees);
  const [weekNumber, setWeekNumber] = useState('');
  const [roleClarity, setRoleClarity] = useState('');
  const [workEnv, setWorkEnv] = useState('');
  const [supportReq, setSupportReq] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!emp.screeningID || !weekNumber) return alert('Select employee and week.');
    setSubmitting(true);
    try {
      await onSubmit({ ...emp, weekNumber, roleClarity, workEnvironment: workEnv, supportRequired: supportReq });
      resetEmp(); setWeekNumber(''); setRoleClarity(''); setWorkEnv(''); setSupportReq('');
    } catch (err) { alert(err.message); }
    setSubmitting(false);
  };

  return (
    <div className="glass" style={{ padding: '1.25rem' }}>
      <h2 style={{ fontSize: '1.125rem', fontWeight: 700, color: 'var(--pink-500)', marginBottom: '1rem' }}>≡ƒôï Week 2 / 3 / 4 Review Form</h2>
      <form onSubmit={handleSubmit} className="space-y-sm">
        <div className="grid-2">
          <EmployeeSearchDropdown employees={employees} selected={emp} onSelect={selectEmployee} onClear={resetEmp} />
          <div>
            <label className="label">Week Number *</label>
            <select className="g-input" value={weekNumber} onChange={e => setWeekNumber(e.target.value)} required>
              <option value="">-- Select --</option>
              <option value="Week 2">Week 2</option>
              <option value="Week 3">Week 3</option>
              <option value="Week 4">Week 4</option>
            </select>
          </div>
        </div>
        <EmployeeInfo data={emp} />
        <div className="grid-2">
          <div><label className="label">Role Clarity & Workload</label><textarea className="g-input" rows={2} value={roleClarity} onChange={e => setRoleClarity(e.target.value)} placeholder="Clear about roles? Occupied enough?" /></div>
          <div><label className="label">Work Environment & Comfort</label><textarea className="g-input" rows={2} value={workEnv} onChange={e => setWorkEnv(e.target.value)} placeholder="Comfortable? Infrastructure? Conversations with RM?" /></div>
        </div>
        <div><label className="label">Support Required</label><textarea className="g-input" rows={2} value={supportReq} onChange={e => setSupportReq(e.target.value)} placeholder="What support does the employee need?" /></div>
        <button className="btn btn-pink" style={{ width: '100%' }} disabled={submitting}>{submitting ? 'Submitting...' : 'Submit Week 2-4 Response'}</button>
      </form>
    </div>
  );
}

// ΓöÇΓöÇΓöÇ ISSUE TRACKER ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function IssueTracker({ issues, onStatusChange, onResolve }) {
  const [statusFilter, setStatusFilter] = useState('');
  const [flagFilter, setFlagFilter] = useState('');
  const [resolveModal, setResolveModal] = useState(null);
  const [resolveNotes, setResolveNotes] = useState('');

  const filtered = issues.filter(i => {
    if (statusFilter && i.Status !== statusFilter) return false;
    if (flagFilter && i['Flag Level'] !== flagFilter) return false;
    return true;
  });

  return (
    <div className="space-y">
      <div className="flex-gap">
        <span style={{ fontSize: '0.75rem', color: 'var(--text-muted)' }}>Filter:</span>
        {['', 'Open', 'In Progress', 'Resolved'].map(s => (
          <button key={s} className={`pill ${statusFilter === s ? 'active' : ''}`} onClick={() => setStatusFilter(s)}>{s || 'All'}</button>
        ))}
        <span style={{ margin: '0 0.25rem', color: 'rgba(255,255,255,0.1)' }}>|</span>
        {['', 'Red', 'Yellow', 'Green'].map(f => (
          <button key={f} className={`pill ${flagFilter === f ? 'active' : ''}`} onClick={() => setFlagFilter(f)}>{f ? (f === 'Red' ? '≡ƒö┤' : f === 'Yellow' ? '≡ƒƒí' : '≡ƒƒó') : 'All Flags'}</button>
        ))}
      </div>

      <div className="glass" style={{ padding: '1rem', overflowX: 'auto' }}>
        <table className="tracker-table">
          <thead>
            <tr>
              <th>Ticket</th><th>Employee</th><th>Type</th><th>Description</th><th>Flag</th><th>Owner</th><th>Status</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(issue => (
              <tr key={issue['Ticket ID']}>
                <td style={{ fontFamily: 'monospace', color: 'var(--purple-400)', fontSize: '0.75rem' }}>{issue['Ticket ID']}</td>
                <td><span style={{ fontSize: '0.75rem' }}>{issue['Employee Name']}</span><br /><span style={{ fontSize: '0.625rem', color: 'var(--text-muted)' }}>{issue['Screening ID']}</span></td>
                <td><span className={`badge ${issue.Type === 'Issue' ? 'badge-red' : 'badge-blue'}`}>{issue.Type}</span></td>
                <td style={{ maxWidth: '200px', fontSize: '0.75rem', color: 'var(--text-secondary)' }}>{issue.Description}</td>
                <td><span className={`badge badge-${issue['Flag Level'] === 'Red' ? 'red' : issue['Flag Level'] === 'Yellow' ? 'yellow' : 'green'}`}>{issue['Flag Level']}</span></td>
                <td style={{ fontSize: '0.75rem', color: 'var(--text-muted)' }}>{issue['Action Owner'] || 'ΓÇö'}</td>
                <td><span className={`badge ${issue.Status === 'Resolved' ? 'badge-green' : issue.Status === 'In Progress' ? 'badge-yellow' : 'badge-orange'}`}>{issue.Status}</span></td>
                <td>
                  {issue.Status !== 'Resolved' ? (
                    <div className="flex-gap">
                      <button className="btn btn-xs" style={{ background: 'rgba(250,204,21,0.2)', color: 'var(--yellow-400)' }} onClick={() => onStatusChange(issue['Ticket ID'], 'In Progress')}>≡ƒöä</button>
                      <button className="btn btn-xs" style={{ background: 'rgba(52,211,153,0.2)', color: 'var(--emerald-400)' }} onClick={() => { setResolveModal(issue); setResolveNotes(''); }}>Γ£à</button>
                    </div>
                  ) : <span style={{ fontSize: '0.625rem', color: 'var(--text-muted)' }}>{issue['Resolution Notes'] || 'Done'}</span>}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {filtered.length === 0 && <p style={{ textAlign: 'center', color: 'var(--text-muted)', padding: '2rem' }}>No issues matching this filter.</p>}
      </div>

      {resolveModal && (
        <div className="modal-overlay" onClick={() => setResolveModal(null)}>
          <div className="modal-box" onClick={e => e.stopPropagation()}>
            <h3 style={{ fontSize: '1.125rem', fontWeight: 700, color: 'var(--emerald-400)', marginBottom: '1rem' }}>Γ£à Resolve: {resolveModal['Ticket ID']}</h3>
            <p style={{ fontSize: '0.875rem', color: 'var(--text-secondary)' }}>{resolveModal['Employee Name']}</p>
            <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', marginBottom: '1rem' }}>{resolveModal.Description}</p>
            <label className="label">Resolution Notes *</label>
            <textarea className="g-input" rows={3} value={resolveNotes} onChange={e => setResolveNotes(e.target.value)} placeholder="How was this resolved?" />
            <div className="flex-gap" style={{ marginTop: '1rem' }}>
              <button className="btn btn-success" style={{ flex: 1 }} onClick={() => { if (!resolveNotes) return alert('Add notes'); onResolve(resolveModal['Ticket ID'], resolveNotes); setResolveModal(null); }}>Mark Resolved</button>
              <button className="btn btn-ghost" onClick={() => setResolveModal(null)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// ΓöÇΓöÇΓöÇ RAISE ISSUE (MULTIPLE) ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ
function RaiseIssue({ employees, onSubmit }) {
  const { selected: emp, selectEmployee, reset: resetEmp } = useEmployeeSelect(employees);
  const [sourceWeek, setSourceWeek] = useState('Week 1');
  const [rows, setRows] = useState([{ type: 'Issue', description: '', flagLevel: 'Yellow', actionOwner: '' }]);
  const [submitting, setSubmitting] = useState(false);

  const addRow = () => setRows([...rows, { type: 'Issue', description: '', flagLevel: 'Yellow', actionOwner: '' }]);
  const removeRow = (idx) => setRows(rows.filter((_, i) => i !== idx));
  const updateRow = (idx, field, val) => { const nr = [...rows]; nr[idx] = { ...nr[idx], [field]: val }; setRows(nr); };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!emp.screeningID) return alert('Select an employee first.');
    const valid = rows.filter(r => r.description.trim());
    if (!valid.length) return alert('Add at least one issue with a description.');
    setSubmitting(true);
    try {
      const payload = valid.map(r => ({ ...r, screeningID: emp.screeningID, name: emp.name, designation: emp.designation, sourceWeek }));
      await onSubmit(payload);
      resetEmp(); setSourceWeek('Week 1'); setRows([{ type: 'Issue', description: '', flagLevel: 'Yellow', actionOwner: '' }]);
    } catch (err) { alert(err.message); }
    setSubmitting(false);
  };

  return (
    <div className="glass" style={{ padding: '1.25rem' }}>
      <h2 style={{ fontSize: '1.125rem', fontWeight: 700, color: 'var(--orange-400)', marginBottom: '1rem' }}>Γ₧ò Raise Issues / Support Tickets</h2>
      <form onSubmit={handleSubmit} className="space-y-sm">
        <div className="grid-2">
          <EmployeeSearchDropdown employees={employees} selected={emp} onSelect={selectEmployee} onClear={resetEmp} />
          <div>
            <label className="label">Source Week</label>
            <select className="g-input" value={sourceWeek} onChange={e => setSourceWeek(e.target.value)}>
              <option value="Week 1">Week 1</option><option value="Week 2">Week 2</option><option value="Week 3">Week 3</option><option value="Week 4">Week 4</option>
            </select>
          </div>
        </div>
        <EmployeeInfo data={emp} />

        <div className="flex-between">
          <h3 style={{ fontSize: '0.875rem', fontWeight: 600, color: 'var(--text-secondary)' }}>Issues / Support Items</h3>
          <button type="button" className="btn btn-sm" style={{ background: 'rgba(139,92,246,0.2)', color: 'var(--purple-400)' }} onClick={addRow}>+ Add Row</button>
        </div>

        {rows.map((row, idx) => (
          <div key={idx} className="issue-row" style={{ padding: '0.75rem' }}>
            <div className="grid-5" style={{ alignItems: 'end' }}>
              <div>
                <label className="label label-xs">#{idx + 1} Type</label>
                <select className="g-input" value={row.type} onChange={e => updateRow(idx, 'type', e.target.value)}>
                  <option value="Issue">≡ƒö┤ Issue</option><option value="Support">≡ƒö╡ Support</option>
                </select>
              </div>
              <div style={{ gridColumn: 'span 2' }}>
                <label className="label label-xs">Description *</label>
                <input className="g-input" value={row.description} onChange={e => updateRow(idx, 'description', e.target.value)} required placeholder="No laptop, 60km commute..." />
              </div>
              <div>
                <label className="label label-xs">Flag Level</label>
                <select className="g-input" value={row.flagLevel} onChange={e => updateRow(idx, 'flagLevel', e.target.value)}>
                  <option value="Red">≡ƒö┤ Red</option><option value="Yellow">≡ƒƒí Yellow</option><option value="Green">≡ƒƒó Green</option>
                </select>
              </div>
              <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'end' }}>
                <div style={{ flex: 1 }}>
                  <label className="label label-xs">Action Owner</label>
                  <input className="g-input" value={row.actionOwner} onChange={e => updateRow(idx, 'actionOwner', e.target.value)} placeholder="IT, Admin..." />
                </div>
                {rows.length > 1 && <button type="button" style={{ fontSize: '0.75rem', color: 'var(--red-400)', background: 'none', border: 'none', cursor: 'pointer', padding: '0.75rem 0.25rem' }} onClick={() => removeRow(idx)}>Γ£ò</button>}
              </div>
            </div>
          </div>
        ))}

        <button className="btn btn-danger" style={{ width: '100%' }} disabled={submitting}>{submitting ? 'Creating tickets...' : `≡ƒÄ½ Create ${rows.length} Ticket(s)`}</button>
      </form>
    </div>
  );
}
